import React from 'react'
import { MenuData } from '../../../Data/Data';


const Menu = () => {

  const [selected, setSelected] = React.useState(false);


  return (
    <div className='menu'>
      {MenuData.map((item, index) => {
        return (
          <div 
            className= {selected ? "menu-items active" : "menu-items"}
            onClick={setSelected}         

            >
              <item.icon />

            </div>
        )
      })}
    </div>
  )
}

export default Menu
