import React from 'react'
import './Updates.css'
import {UilNewspaper, UilStopCircle} from "@iconscout/react-unicons"
import useNewApi from './useNewApi';
import Truncate from 'react-truncate';
import sourceNews from '../../../imgs/img1.jpeg'

const Updates = () => {
  const { data, error, loading } = useNewApi();

  console.log(data)
  // const data = false;

  const updateData = data ? <NewsContent /> : <ErrorContent />


  function NewsContent() {
    const articleSource = !data ? "loading..." : data.articles[0].source.name
    const articleImg = !data ? "loading" : data.articles[0].urlToImage
    const articleTitle = !data ? "loading..." : data.articles[0].title
    const articleDescription = !data ? "loading..." : data.articles[0].content
    const articleLink = !data ? "#" : data.articles[0].url
    const articleAuthor = !data ? "Website" : data.articles[0].author

    return (
      <div className="Updates">
        <div className="update">
          <div className="news--top">
            <UilNewspaper className='news--icon' />
            <span className='news--top--title'>Top news</span>
          </div>
          <div className="author--details">
            <img src={articleImg} alt="" />
            <span className="author--source muted-text">{articleSource}</span>
          </div>

          <div className="news--content">
            <span className='news--headline' >
              <span>{articleTitle}</span>
            </span>
            <br />
                <span className='author'>Author </span>
                <span className="muted-text bold-dark">{articleAuthor}</span>
            <br />
            <div>
              <Truncate lines={4} ellipsis={<span>...</span>} className='news--description' >
                <span>{articleDescription}</span>
              </Truncate>
            </div>
            <span><a href={articleLink} target="_blank">Read more...</a></span>
            <br />

          </div>
        </div>
        <span className="muted-text article--time">
              3 mins ago
            </span>
      </div>
    )
  }



    return (
        <>
          {updateData}
        </>
      );
}

export default Updates


function ErrorContent() {
      return (
        <div className="news--error glass">
              <UilStopCircle className='danger--sign' />
              <span>Error loading news</span>
              <span>Network Error...</span>
        </div>
      )
}

function ArticleAuthor() {
