    true: {
      left : '0'
    },
    false:{
      left : '-60%'
    }
  }

  return (
    <div className='Sidebar'>
        <Brand />
        <Profile />
        <Menu />

        <>
      <div className="bars" style={expanded?{left: '60%'}:{left: '5%'}} onClick={()=>setExpaned(!expanded)}>
