// Sidebar imports
import {
    UilEstate,
    UilClipboardAlt,
    UilChart,
    UilTransaction,
    UilUserCircle,
    UilSetting
  } from "@iconscout/react-unicons";

  // Analytics Cards imports
  import { UilUsdSquare, UilMoneyWithdrawal } from "@iconscout/react-unicons";
  import { keyboard } from "@testing-library/user-event/dist/keyboard";

  // Sidebar Data
  export const MenuData = [
    {
      icon: UilEstate,
      heading: "Dashboard",
    },
    {
      icon: UilUserCircle,
      heading: "Profile",
    },
    {
      icon: UilTransaction,
      heading: "Transact",
    },
    {
      icon: UilChart,
      heading: "Analytics",
    },
    {
      icon: UilSetting,
      heading: 'Settings'
    },
  ];

  // Personal data
  export const personalData = {
    name: 'Charles Duya',
    age: 43,
    Target: {
      savings: 5000,
      income: 25000,
      expenses: 10000
    },
    bio: "If you are not willing to do the things other people don\'t want to do, you wont have the things other peoples don\'t",
    img: '#'
  }

  // Financial data

  export const financialData = {
    balance: 13000,
    lastMonth: {
      income: 12000,
      expenses: 49000,
      savings: 7000
    },
    thisMonth: {
      income: 130000,

    }

  }

  // Analytics Cards Data
  export const cardsData = [
    {
      title: "Income",
      color: {
        backGround: "linear-gradient(180deg, #bb67ff 0%, #c484f3 100%)",
        boxShadow: "0px 10px 20px 0px #e0c6f5",
      },
      barValue: 10,
      value: "25,970",
      png: UilUsdSquare,
      series: [
        {
          name: "Income",
          data: [31, 40, 28, 51, 42, 109, 100],
        },
      ],
    },
    {
      title: "Expenses",
      color: {
        backGround: "linear-gradient(180deg, #FF919D 0%, #FC929D 100%)",
        boxShadow: "0px 10px 20px 0px #FDC0C7",
      },
      barValue: 80,
      value: "14,270",
      png: UilMoneyWithdrawal,
      series: [
        {
          name: "Expenses",
          data: [10, 100, 50, 70, 80, 30, 40],
        },
      ],
    },
    {
      title: "Savings",
      color: {
        backGround:
          "linear-gradient(rgb(248, 212, 154) -146.42%, rgb(255 202 113) -46.42%)",
        boxShadow: "0px 10px 20px 0px #F9D59B",
      },
      barValue: 60,
      value: "4,270",
      png: UilClipboardAlt,
      series: [
        {
          name: "Savings",
          data: [10, 25, 15, 30, 12, 15, 20],
        },
      ],
    },
  ];

  export const dummyData = {

    options: {
      chart: {
        type: "area",
        height: "auto",
      },

      dropShadow: {
        enabled: false,
        enabledOnSeries: undefined,
        top: 0,
        left: 0,
        blur: 3,
        color: "#000",
        opacity: 0.35,
      },

      fill: {
        colors: ["#fff"],
        type: "gradient",
      },
      dataLabels: {
        enabled: false,
      },
      stroke: {
        curve: "smooth",
        colors: ["white"],
      },
      tooltip: {
        x: {
          format: "dd/MM/yy HH:mm",
        },
      },
      grid: {
        show: true,
      },
      xaxis: {
        type: "datetime",
        categories: [
          "2022-09-19T00:00:00.000Z",
          "2023-01-19T01:30:00.000Z",
          "2023-01-19T02:30:00.000Z",
          "2023-01-19T03:30:00.000Z",
          "2023-01-19T04:30:00.000Z",
          "2023-01-19T05:30:00.000Z",
          "2023-01-19T06:30:00.000Z",
        ],
      },
    },
  };

  export const rows = [
    {name: 'Chapo zimeweid vizii', transactionId: 18908424, date: '17 January 2023', amount: 130, type: 'credit'},
    {name: 'Samaki ya Lilian', transactionId: 18908424, date: '16 January 2023', amount: 200, type: 'debit'},
    {name: 'Njiva za Lisahs', transactionId: 18908424, date: '15 January 2023', amount: 230, type: 'debit'},
    {name: 'Food expe ya Aticah\'s', transactionId: 18908424, date: '13 January 2023', amount: 535, type: 'credit'}
  ]

  export const reviewData = {
    series: [
      {
        name: "Review",
        data: [10, 50, 30, 90, 40, 120, 100],
      },
    ],
    options: {
      chart: {
        type: "area",
        height: "auto",
      },

      fill: {
        colors: ["#fff"],
        type: "gradient",
      },
      dataLabels: {
        enabled: false,
      },
      stroke: {
        curve: "smooth",
        colors: ["#ff929f"],
      },
      tooltip: {
        x: {
          format: "dd/MM/yy HH:mm",
        },
      },
      grid: {
        show: false,
      },
      xaxis: {
        type: "datetime",
        categories: [
          "2018-09-19T00:00:00.000Z",
          "2018-09-19T01:30:00.000Z",
          "2018-09-19T02:30:00.000Z",
          "2018-09-19T03:30:00.000Z",
          "2018-09-19T04:30:00.000Z",
          "2018-09-19T05:30:00.000Z",
          "2018-09-19T06:30:00.000Z",
        ],
      },
      yaxis: {
        show: false
      },
      toolbar:{
        show: false
      }
    },
  };
