import React from 'react'
import './Updates.css'
import {UilNewspaper, UilStopCircle} from "@iconscout/react-unicons"
// import useNewApi from './useNewApi';
import Truncate from 'react-truncate';
import sourceNews from '../../../imgs/img1.jpeg'

const Updates = () => {
  // const { data, error, loading } = useNewApi();

  // console.log(data)
  const data = true;

  const updateData = data ? <NewsContent /> : <ErrorContent />

    return (
        {updateData}
      );
}

export default Updates

// {data.articles.map((article, index) => (
//   <div key={index}>
//     <h2>{article.title}</h2>
//     <p>{article.description}</p>
//   </div>
// ))}

function ErrorContent() {
      return (
        <div className="Updates">
          <div className="news--error">
          <div className="news--error--top">
              <div className="error--img"></div>
              <div className="error--source"></div>
          </div>
          <div className="news--error--middle">
              <UilStopCircle />
              <div className="error--message">
                <span>Error loading news</span>
              </div>
          </div>
          <div className="news--error--bottom">

          </div>
        </div>
        </div>
      )
}

function NewsContent() {
  return (
    <div className="Updates">
      <div className="update">
        <div className="news--top">
          <UilNewspaper className='news--icon' />
          <span className='news--top--title'>Top news</span>
        </div>
        <div className="author--details">
          <img src={sourceNews} alt="" />
          <span className="author--source muted-text">Source of article</span>
        </div>

        <div className="news--content">
          <span className='news--headline' >
            <span>This morning someone saying things was arrested in his house saying things he was saying</span>
          </span>
          <br />
          <span className='author'>Author </span><span className="muted-text bold-dark">Charles Duya</span>
          <br />
          <div>
            <Truncate lines={4} ellipsis={<span>...</span>} className='news--description' >
              <span>He was saying a lot of things that were not nice to many people, who thought that the things he was saying were not nice. Police report that he was in a state of shock when he found out he way saying things that were being said by him in the moment when he was saying them. Just recently, he was found saying other things that also did not please anyone. This led to other people saying that he was saying things that he was saying to the local authorities who have arrested him for saying things that he was saying.</span>
            </Truncate>
          </div>
          <span><a href="">Read more...</a></span>
          <br />

        </div>
      </div>
      <span className="muted-text article--time">
            3 mins ago
          </span>
    </div>
  )
}
