import React from 'react'
import './Updates.css'
import {UilNewspaper} from "@iconscout/react-unicons"
import useNewApi from './useNewApi';
import Truncate from 'react-truncate';

const Updates = () => {
  const { data, error, loading } = useNewApi();

  console.log(data)

    return (
        <div className="Updates">
          <div className="update">
            <div className="news--top">
              <UilNewspaper className='news--icon' />
              <span className='news--top--title'>Top news</span>
            </div>

            <div className="news--content">
              <span className='news--headline'>
                This morning someone saying things was arrested in his house saying things he was saying
              </span>
              <br />
              <span className='author'>Author </span><span className="muted-text bold-dark">Charles Duya</span>
              <br />
              <div>
                <Truncate lines={4} ellipsis={<span>...</span>} className='news--description' >
                He was saying a lot of things that were very not nice to many people that though that they were not nice. Police report that he was in a state of shock when he found out he way saying things that were being said by him in the moment when he was saying them. Just recently, he was found saying other things that also did not please anyone. This led to other people saying that he was saying things that he was saying to the local authorities who have arrested him for saying things that he was saying.
                </Truncate>
              </div>
              <span><a href="">Read more...</a></span>
              <br />
              <span className="muted-text">
                3 mins ago
              </span>
            </div>
            {/* <UilStopCircle */}
          </div>
        </div>
      );
}

export default Updates

