import React from 'react'
import { useState, useEffect } from 'react';

const useNewApi = ({category}) => {

    const [data, setData] = useState(null);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    // const [category, setCategory] = useState(2);

    const location = "us"

    const queries = ['business', 'investing', 'ai']


    const catenewsheadlinesurl = 'http://newsapi.org/v2/top-headlines?q=' + queries[category] + '&country=' + location + '&category=business&language=en&pageSize=30&apiKey=3094d12201084bda8a6094cd0d4de114'

    useEffect(() => {
      const fetchData = async () => {
        setLoading(true);
        try {
          const response = await fetch(catenewsheadlinesurl)
          const json = await response.json();
          setData(json);
        } catch (err) {
          setError(err);
        }
        setLoading(false);
      };
      fetchData();
    }, [category]);

    return { data, error, loading };
}

export default useNewApi
