import {useState, useEffect} from 'react'
import './Transact.css'
import transactBackground from '../../../../imgs/tabs.jpg'
import { rows } from '../../../../Data/Data'

const Transact = () => {
  const [Tdata, setTdata] = useState(JSON.parse(localStorage.getItem('Transactions')));
  const [updated, setUpdated]=useState(false);

  useEffect(()=>{
    localStorage.setItem('Transactions', JSON.stringify(Tdata))
    },[Tdata])

  const [transaction, setTransaction] = useState(
    {
      amount: '',
      description: '',
      transactionId: '',
      date: '',
      type: ''
    }
  )

  // console.log(transaction)

  function handleChange(event) {
    setTransaction(prevData => {
      return {
        ...prevData,
        [event.target.name]: event.target.value
      }
    })
  }

  function addTransaction(event) {
    const exactTime = new Date();
    let transactionTime = "" + exactTime;
    setTransaction(prevData => {
      return {
        ...prevData,
        type: event.target.name,
        date: transactionTime.substring(0, 24),
        transactionId: "" + new Date().getTime()
      }
    })
    setUpdated(true)
  }

  if (updated) {
    setTdata(prevData => {
      return [
        transaction,
        ...prevData
      ]
    });
    setTransaction({
        amount: '',
        description: '',
        transactionId: '',
        date: '',
        type: ''
    })
    setUpdated(false)
  }

  return (
    <div style={{background: `url(${transactBackground})`}} className='transact-wrapper glass'>
      <div className="transact-wrapper">
        <div className="input-area">
          <label htmlFor="amount">Enter amount: </label>
          <input
           type="number"
           placeholder='Minimum of 1 shilling'
           onChange={handleChange}
           name='amount'
           value={transaction.amount}
           />
          <label htmlFor="narration">Narration</label>
          <input 
            type="text"
            placeholder='enter description'
            onChange={handleChange}
            name='description'
            value={transaction.description}
          />

        </div>
      </div>
    </div> 
  )
}

export default Transact
